(102, '18', 'TP5替换字符串PUBLIC', '', '', '', '<pre class=\"brush:php;toolbar:false\">thinkphp-&gt;library-&gt;think-&gt;view.php\r\npublic&nbsp;function&nbsp;__construct($engine&nbsp;=&nbsp;[],&nbsp;$replace&nbsp;=&nbsp;[])\r\n{\r\n&nbsp;&nbsp;&nbsp;&nbsp;//&nbsp;初始化模板引擎\r\n&nbsp;&nbsp;&nbsp;&nbsp;$this-&gt;engine($engine);\r\n&nbsp;&nbsp;&nbsp;&nbsp;//&nbsp;基础替换字符串\r\n&nbsp;&nbsp;&nbsp;&nbsp;$request&nbsp;=&nbsp;Request::instance();\r\n&nbsp;&nbsp;&nbsp;&nbsp;$base&nbsp;&nbsp;&nbsp;&nbsp;=&nbsp;$request-&gt;root();\r\n&nbsp;&nbsp;&nbsp;&nbsp;$root&nbsp;&nbsp;&nbsp;&nbsp;=&nbsp;strpos($base,&nbsp;&#39;.&#39;)&nbsp;?&nbsp;ltrim(dirname($base),&nbsp;DS)&nbsp;:&nbsp;$base;\r\n&nbsp;&nbsp;&nbsp;&nbsp;if&nbsp;(&#39;&#39;&nbsp;!=&nbsp;$root)&nbsp;{\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;$root&nbsp;=&nbsp;&#39;/&#39;&nbsp;.&nbsp;ltrim($root,&nbsp;&#39;/&#39;);\r\n&nbsp;&nbsp;&nbsp;&nbsp;}\r\n&nbsp;&nbsp;&nbsp;&nbsp;$baseReplace&nbsp;=&nbsp;[\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&#39;__ROOT__&#39;&nbsp;&nbsp;&nbsp;=&gt;&nbsp;$root,\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&#39;__URL__&#39;&nbsp;&nbsp;&nbsp;&nbsp;=&gt;&nbsp;$base&nbsp;.&nbsp;&#39;/&#39;&nbsp;.&nbsp;$request-&gt;module()&nbsp;.&nbsp;&#39;/&#39;&nbsp;.&nbsp;Loader::parseName($request-&gt;controller()),\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&#39;__STATIC__&#39;&nbsp;=&gt;&nbsp;$root&nbsp;.&nbsp;&#39;/static&#39;,\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&#39;__CSS__&#39;&nbsp;&nbsp;&nbsp;&nbsp;=&gt;&nbsp;$root&nbsp;.&nbsp;&#39;/static/Css&#39;,\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&#39;__JS__&#39;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;=&gt;&nbsp;$root&nbsp;.&nbsp;&#39;/static/Js&#39;,\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&#39;__APP__&#39;&nbsp;&nbsp;&nbsp;=&gt;&nbsp;$base&nbsp;.&nbsp;&#39;/index&#39;,\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&#39;__PUBLIC__&#39;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;=&gt;&nbsp;$root&nbsp;.&nbsp;&#39;/static&#39;,\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&#39;__IMAGE__&#39;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;=&gt;&nbsp;$root&nbsp;.&nbsp;&#39;/static/Image&#39;,\r\n&nbsp;&nbsp;&nbsp;&nbsp;];\r\n&nbsp;&nbsp;&nbsp;&nbsp;$this-&gt;replace&nbsp;=&nbsp;array_merge($baseReplace,&nbsp;(array)&nbsp;$replace);\r\n}</pre><p><br/></p>', '2019-03-10 14:51:38', 0, 0, 0, '', '', ''),
(96, '14', 'vue常见的dom操作', '', 'vue常见的dom操作', '', '<pre class=\"brush:js;toolbar:false\">methods:&nbsp;{\r\nclickfun(e)&nbsp;{\r\n//&nbsp;e.target&nbsp;是你当前点击的元素\r\n//&nbsp;e.currentTarget&nbsp;是你绑定事件的元素\r\n&nbsp;&nbsp;&nbsp;&nbsp;#获得点击元素的前一个元素\r\n&nbsp;&nbsp;&nbsp;&nbsp;e.currentTarget.previousElementSibling.innerHTML\r\n&nbsp;&nbsp;&nbsp;&nbsp;#获得点击元素的第一个子元素\r\n&nbsp;&nbsp;&nbsp;&nbsp;e.currentTarget.firstElementChild\r\n&nbsp;&nbsp;&nbsp;&nbsp;#&nbsp;获得点击元素的下一个元素\r\n&nbsp;&nbsp;&nbsp;&nbsp;e.currentTarget.nextElementSibling\r\n&nbsp;&nbsp;&nbsp;&nbsp;#&nbsp;获得点击元素中id为string的元素\r\n&nbsp;&nbsp;&nbsp;&nbsp;e.currentTarget.getElementById(&quot;string&quot;)\r\n&nbsp;&nbsp;&nbsp;&nbsp;#&nbsp;获得点击元素的string属性\r\n&nbsp;&nbsp;&nbsp;&nbsp;e.currentTarget.getAttributeNode(&#39;string&#39;)\r\n&nbsp;&nbsp;&nbsp;&nbsp;#&nbsp;获得点击元素的父级元素\r\n&nbsp;&nbsp;&nbsp;&nbsp;e.currentTarget.parentElement\r\n&nbsp;&nbsp;&nbsp;&nbsp;#&nbsp;获得点击元素的前一个元素的第一个子元素的HTML值\r\n&nbsp;&nbsp;&nbsp;&nbsp;e.currentTarget.previousElementSibling.firstElementChild.innerHTML\r\n&nbsp;\r\n&nbsp;&nbsp;&nbsp;&nbsp;}\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;}</pre><p><br/></p>', '2019-01-11 06:34:01', 0, 6, 0, '', '', ''),
(99, '19', 'object-fit兼容ie的替换法', '', '', '', '<p>js方法</p><pre class=\"brush:js;toolbar:false\">if&nbsp;(!!window.ActiveXObject&nbsp;||&nbsp;&quot;ActiveXObject&quot;&nbsp;in&nbsp;window)&nbsp;{\r\n&nbsp;&nbsp;&nbsp;&nbsp;if&nbsp;($(&quot;.pic&quot;).length)&nbsp;{\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;$(&quot;.pic&quot;).each(function&nbsp;()&nbsp;{\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;$(this).addClass(&quot;pic_ie_on&quot;);\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;var&nbsp;str=&quot;&quot;&nbsp;;\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;$(this).children(&quot;.needCover&quot;).remove();\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;$(this).append(str);\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;});\r\n&nbsp;&nbsp;&nbsp;&nbsp;}\r\n}</pre><p>css样式(和普通案例页样式相同)</p><pre class=\"brush:css;toolbar:false\">.pic_ie_on{\r\n&nbsp;&nbsp;&amp;:hover{\r\n&nbsp;&nbsp;&nbsp;&nbsp;.pic_ie{\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&amp;:before{\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;opacity:&nbsp;1;\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;}\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&amp;:after{\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;width:&nbsp;85%;\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;height:&nbsp;85%;\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;opacity:&nbsp;1;\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;}\r\n&nbsp;&nbsp;&nbsp;&nbsp;}\r\n&nbsp;&nbsp;}\r\n&nbsp;&nbsp;.pic_ie{\r\n&nbsp;&nbsp;&nbsp;&nbsp;width:&nbsp;100%;\r\n&nbsp;&nbsp;&nbsp;&nbsp;height:&nbsp;100%;\r\n&nbsp;&nbsp;&nbsp;&nbsp;background-size:&nbsp;cover;\r\n&nbsp;&nbsp;&nbsp;&nbsp;background-position:&nbsp;center;\r\n&nbsp;&nbsp;&nbsp;&nbsp;@include&nbsp;transition(0.5s);\r\n&nbsp;&nbsp;&nbsp;&nbsp;position:&nbsp;relative;\r\n&nbsp;&nbsp;&nbsp;&nbsp;&amp;:before{\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;content:&nbsp;&#39;&#39;;\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;@include&nbsp;center;\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;width:&nbsp;100%;\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;height:&nbsp;100%;\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;background:&nbsp;rgba(0,0,0,0.5);\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;z-index:&nbsp;3;\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;opacity:&nbsp;0;\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;@include&nbsp;transition(0.3s);\r\n&nbsp;&nbsp;&nbsp;&nbsp;}\r\n&nbsp;&nbsp;&nbsp;&nbsp;&amp;:after{\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;content:&nbsp;&#39;&#39;;\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;@include&nbsp;center;\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;width:&nbsp;99%;\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;height:&nbsp;99%;\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;border:&nbsp;1px&nbsp;solid&nbsp;#fff;\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;z-index:&nbsp;4;\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;background:&nbsp;none;\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;opacity:&nbsp;0;\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;@include&nbsp;transition(0.3s);\r\n&nbsp;&nbsp;&nbsp;&nbsp;}\r\n&nbsp;&nbsp;}\r\n}</pre><p><br/></p>', '2019-02-22 03:31:42', 0, 0, 0, '', '', ''),
(100, '18', 'php请求的两种方法', '', '', '', '<p>方法一:</p><pre class=\"brush:php;toolbar:false\">public&nbsp;function&nbsp;https_request($url,&nbsp;$data&nbsp;=&nbsp;null){\r\n&nbsp;&nbsp;&nbsp;&nbsp;$curl&nbsp;=&nbsp;curl_init();\r\n&nbsp;&nbsp;&nbsp;&nbsp;curl_setopt($curl,CURLOPT_URL,$url);\r\n&nbsp;&nbsp;&nbsp;&nbsp;curl_setopt($curl,&nbsp;CURLOPT_SSL_VERIFYPEER,&nbsp;FALSE);\r\n&nbsp;&nbsp;&nbsp;&nbsp;curl_setopt($curl,&nbsp;CURLOPT_SSL_VERIFYHOST,&nbsp;FALSE);\r\n&nbsp;&nbsp;&nbsp;&nbsp;if&nbsp;(!empty($data)){\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;curl_setopt($curl,&nbsp;CURLOPT_POST,&nbsp;1);\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;curl_setopt($curl,&nbsp;CURLOPT_POSTFIELDS,&nbsp;$data);\r\n&nbsp;&nbsp;&nbsp;&nbsp;}\r\n&nbsp;&nbsp;&nbsp;&nbsp;curl_setopt($curl,&nbsp;CURLOPT_RETURNTRANSFER,&nbsp;1);\r\n&nbsp;&nbsp;&nbsp;&nbsp;$output&nbsp;=&nbsp;curl_exec($curl);\r\n&nbsp;&nbsp;&nbsp;&nbsp;curl_close($curl);\r\n&nbsp;&nbsp;&nbsp;&nbsp;return&nbsp;$output;\r\n}</pre><pre class=\"brush:php;toolbar:false\">$url&nbsp;=&nbsp;&quot;http://xxx/test.php?type=cat&quot;;\r\n$result&nbsp;=&nbsp;$this-&gt;https_request($url);\r\n//果需要将json格式转化为数组格式\r\n$result&nbsp;=json_decode($result&nbsp;,true);</pre><p><br/></p><p>方法二:</p><pre class=\"brush:php;toolbar:false\">function&nbsp;send_post($url,&nbsp;$post_data)&nbsp;{\r\n&nbsp;&nbsp;&nbsp;&nbsp;$postdata&nbsp;=&nbsp;http_build_query($post_data);\r\n&nbsp;&nbsp;&nbsp;&nbsp;$options&nbsp;=&nbsp;array(\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&#39;http&#39;&nbsp;=&gt;&nbsp;array(\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&#39;method&#39;&nbsp;=&gt;&nbsp;&#39;POST&#39;,\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&#39;header&#39;&nbsp;=&gt;&nbsp;&#39;Content-type:application/x-www-form-urlencoded&#39;,\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&#39;content&#39;&nbsp;=&gt;&nbsp;$post_data,\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&#39;timeout&#39;&nbsp;=&gt;&nbsp;60&nbsp;*&nbsp;60&nbsp;//&nbsp;超时时间（单位:s）\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;)\r\n&nbsp;&nbsp;&nbsp;&nbsp;);\r\n&nbsp;&nbsp;&nbsp;&nbsp;$context&nbsp;=&nbsp;stream_context_create($options);\r\n&nbsp;&nbsp;&nbsp;&nbsp;$result&nbsp;=&nbsp;file_get_contents($url,&nbsp;false,&nbsp;$context);\r\n&nbsp;&nbsp;&nbsp;&nbsp;return&nbsp;$result;\r\n}</pre><pre class=\"brush:php;toolbar:false\">$url&nbsp;=&nbsp;&quot;http://xxx/web/test.php&quot;;\r\n$data=array();\r\n$data[&quot;type&quot;]=&quot;cat&quot;;\r\n$result=$this-&gt;send_post($url,&nbsp;json_encode($data));\r\n$result=json_decode($result);</pre><p><br/></p>', '2019-02-22 11:21:49', 0, 2, 0, '', '', ''),
(103, '18', 'TP5 model样例', '', '', '', '<pre class=\"brush:php;toolbar:false\">&lt;?php\r\nnamespace&nbsp;app\\index\\model;\r\n\r\nuse&nbsp;think\\Model;\r\n\r\nclass&nbsp;Article&nbsp;extends&nbsp;Model\r\n{\r\n&nbsp;&nbsp;&nbsp;&nbsp;//&nbsp;设置当前模型对应的完整数据表名称\r\n&nbsp;&nbsp;&nbsp;&nbsp;protected&nbsp;$table&nbsp;=&nbsp;&#39;qf_article&#39;;\r\n\r\n&nbsp;&nbsp;&nbsp;&nbsp;//&nbsp;设置当前模型的数据库连接\r\n&nbsp;&nbsp;&nbsp;&nbsp;protected&nbsp;$connection&nbsp;=&nbsp;[\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;//&nbsp;数据库类型\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&#39;type&#39;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;=&gt;&nbsp;&#39;mysql&#39;,\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;//&nbsp;服务器地址\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&#39;hostname&#39;&nbsp;&nbsp;&nbsp;&nbsp;=&gt;&nbsp;&#39;127.0.0.1&#39;,\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;//&nbsp;数据库名\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&#39;database&#39;&nbsp;&nbsp;&nbsp;&nbsp;=&gt;&nbsp;&#39;vueadmin_qf&#39;,\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;//&nbsp;数据库用户名\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&#39;username&#39;&nbsp;&nbsp;&nbsp;&nbsp;=&gt;&nbsp;&#39;root&#39;,\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;//&nbsp;数据库密码\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&#39;password&#39;&nbsp;&nbsp;&nbsp;&nbsp;=&gt;&nbsp;&#39;root&#39;,\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;//&nbsp;数据库编码默认采用utf8\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&#39;charset&#39;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;=&gt;&nbsp;&#39;utf8&#39;,\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;//&nbsp;数据库表前缀\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&#39;prefix&#39;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;=&gt;&nbsp;&#39;qf_&#39;,\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;//&nbsp;数据库调试模式\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&#39;debug&#39;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;=&gt;&nbs