(125, '18', 'php的几个无限极分类', '', '几个不同场景不同思想的方法', '', '<pre class=\"brush:php;toolbar:false\">public&nbsp;function&nbsp;genOption($arr,$step=0,$style=&quot;&amp;nbsp&amp;nbsp&amp;nbsp&amp;nbsp&amp;nbsp&amp;nbsp&quot;){\r\n&nbsp;&nbsp;&nbsp;&nbsp;//static的定义语句只会被执行一次，但是它的值会被函数记住，直到程序终止\r\n&nbsp;&nbsp;&nbsp;&nbsp;static&nbsp;$str&nbsp;=&#39;&#39;;\r\n&nbsp;&nbsp;&nbsp;&nbsp;$symbol=&#39;&#39;;\r\n&nbsp;&nbsp;&nbsp;&nbsp;for&nbsp;($i=0;&nbsp;$i&lt;$step;&nbsp;$i++)&nbsp;{\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;$symbol=$symbol.$style;\r\n&nbsp;&nbsp;&nbsp;&nbsp;}\r\n&nbsp;&nbsp;&nbsp;&nbsp;foreach($arr&nbsp;as&nbsp;$key=&gt;$value){\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;if(isset($value[&#39;son&#39;])){\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;$str=$str.&quot;&lt;option&nbsp;value=&#39;{$value[&quot;id&quot;]}&#39;&gt;{$symbol}{$value[&quot;name&quot;]}&lt;/option&gt;&quot;;\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;$this-&gt;genOption($value[&#39;son&#39;],$step+1);\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;}\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;else{\r\n\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;$str=$str.&quot;&lt;option&nbsp;value=&#39;{$value[&quot;id&quot;]}&#39;&gt;{$symbol}{$value[&quot;name&quot;]}&lt;/option&gt;&quot;;\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;}\r\n&nbsp;&nbsp;&nbsp;&nbsp;}\r\n&nbsp;&nbsp;&nbsp;&nbsp;return&nbsp;$str;\r\n}</pre><p><br/></p><pre class=\"brush:php;toolbar:false\">public&nbsp;function&nbsp;getTree($id=1){\r\n&nbsp;&nbsp;&nbsp;&nbsp;if&nbsp;(!$id)&nbsp;return&nbsp;false;\r\n\r\n&nbsp;&nbsp;&nbsp;&nbsp;if&nbsp;($id&nbsp;&gt;&nbsp;0&nbsp;&amp;&amp;&nbsp;$id&nbsp;&lt;&nbsp;4)&nbsp;{\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;$ids=&nbsp;Db::query(&quot;select&nbsp;*&nbsp;from&nbsp;qf_type&nbsp;where&nbsp;code&nbsp;like&nbsp;&#39;$id,%&#39;&quot;);\r\n\r\n&nbsp;&nbsp;&nbsp;&nbsp;}&nbsp;else&nbsp;{\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;$ids&nbsp;=&nbsp;Db::query(&quot;select&nbsp;*&nbsp;from&nbsp;qf_type&nbsp;where&nbsp;code&nbsp;like&nbsp;&#39;%,$id,%&#39;&quot;);\r\n&nbsp;&nbsp;&nbsp;&nbsp;}\r\n\r\n&nbsp;&nbsp;&nbsp;&nbsp;//第一步&nbsp;构造数据\r\n&nbsp;&nbsp;&nbsp;&nbsp;//深拷贝\r\n&nbsp;&nbsp;&nbsp;&nbsp;$items&nbsp;=&nbsp;array();\r\n&nbsp;&nbsp;&nbsp;&nbsp;foreach($ids&nbsp;as&nbsp;$value){\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;$items[$value[&#39;id&#39;]]&nbsp;=&nbsp;$value;\r\n&nbsp;&nbsp;&nbsp;&nbsp;}\r\n\r\n&nbsp;&nbsp;&nbsp;&nbsp;//第二部&nbsp;遍历数据&nbsp;生成树状结构\r\n&nbsp;&nbsp;&nbsp;&nbsp;//&amp;为变量的引用\r\n&nbsp;&nbsp;&nbsp;&nbsp;foreach($items&nbsp;as&nbsp;$key&nbsp;=&gt;&nbsp;$value){\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;if(isset($items[$value[&#39;parent&#39;]])){\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;$items[$value[&#39;parent&#39;]][&#39;son&#39;][]&nbsp;=&nbsp;&amp;$items[$key];\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;}else{\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;$tree[]&nbsp;=&nbsp;&amp;$items[$key];\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;}\r\n&nbsp;&nbsp;&nbsp;&nbsp;}\r\n&nbsp;&nbsp;&nbsp;&nbsp;return&nbsp;$tree;\r\n}</pre><p><br/></p>', '2019-03-12 13:20:48', 0, 0, 0, '', '', ''),
